CREATE TABLE IF NOT EXISTS user_entity (
    id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, -- ★ ここを修正
    -- または id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
    -- あるいは id INTEGER IDENTITY PRIMARY KEY,
    name VARCHAR(255) NOT NULL UNIQUE, -- nameはUNIQUEであるべき（ログイン名なので）
    password VARCHAR(255) NOT NULL,
    roles VARCHAR(255) NOT NULL
);
